import React, { useState, useEffect } from "react";
import './App.css';
import SearchInput from "./components/SearchInput";
import IWidget from "./interfaces/IWidget"
import IFilter from "./interfaces/IFilter";
import { genericSearch } from "./utils/genericSearch";
import { genericFilter } from "./utils/genericFilter";
import { Filters } from "./components/Filters";
import { Container, Row, Col, CardColumns } from 'reactstrap';

function App() {
  const [query, setQuery] = useState<string>("");
  const [data, setData] = useState([]);

  const [activeFilters, setActiveFilters] = useState<Array<IFilter<IWidget>>>(
    []
  );

  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then((response) => {
        if (response.status !== 200) {
          console.log('Looks like there was a problem. Status Code: ' +
            response.status);
          return;
        }

        // Examine the text in the response
        response.json().then((lam) => {
          setData(lam);
        });
      }
      )
      .catch(function (err) {
        console.log('Fetch Error :-S', err);
      });
    // return () => {
    //   cleanup
    // }
  }, [])
  const resultdata = data
    .filter((res) =>
      genericSearch<IWidget>(res, ["name", "email"], query)
    )
    .filter((res) => genericFilter<IWidget>(res, activeFilters))
const columns = [
  {
    name: "id",
    label: "ID"
  },
  {
    name: "name",
    label: "Name"
  },
  {
    name: "username",
    label: "Username"
  },
  {
    name: "email",
    label: "Email"
  },
  {
    name: "phone",
    label: "Phone"
  },
  {
    name: "website",
    label: "Website"
  },
  {
    name: "company",
    label: "Company"
  },

]
  return (
    <div className="App">
      {resultdata.length > 0 && (
        <Container>
          <Row md={4}>
            <Col style={{marginTop:"5em", marginBottom:"2em"}}>
              <SearchInput onChangeSearchQuery={(query) => setQuery(query)} />

            </Col>
            <Col >
              <table >
                <tr>
                  {CardColumns.map(column => 
                  <th>{column.name}</th>
                  )}
                </tr>
                {resultdata.map((widget) => {
                  const emailtTo = widget.email;
                  const phone = widget.phone;
                  return (
                  <tr>
                    <td>{widget.id}</td>
                    <td>{widget.name}</td>
                    <td><a style={{textDecoration:"none"}} href={`mailto:${emailtTo}`}>{widget.email}</a></td>
                    <td><a style={{textDecoration:"none"}} href={`tel:${phone}`}>{widget.phone}</a></td>
                    <td>{widget.website}</td>
                    <td>{widget.company.name}</td>
                  </tr>
                  )
                  })}
                <div>
                </div>
              </table>
            </Col>
          </Row>
        </Container>
      )}
    </div>
  );
}

export default App;
