import React, { useState, useEffect } from "react";
import './App.css';
import SearchInput from "./components/SearchInput";
import IWidget from "./interfaces/IWidget"
import IFilter from "./interfaces/IFilter";
import { genericSearch } from "./utils/genericSearch";
import { genericFilter } from "./utils/genericFilter";
// import { Filters } from "./components/Filters";
import {FilterFields} from "./components/FilterFields"
import { Container, Row, Col, CardColumns } from 'reactstrap';

function App() {
  const [query, setQuery] = useState<string>("");
  const [data, setData] = useState([]);
  const [show, setShow] = useState(true);
  const [activeFilters, setActiveFilters] = useState<Array<IFilter<IWidget>>>(
    []
  );

  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then((response) => {
        if (response.status !== 200) {
          console.log('Looks like there was a problem. Status Code: ' +
            response.status);
          return;
        }

        // Examine the text in the response
        response.json().then((lam) => {
          setData(lam);
        });
      }
      )
      .catch(function (err) {
        console.log('Fetch Error :-S', err);
      });
    // return () => {
    //   cleanup
    // }
  }, [])
  const resultdata = data
    .filter((res) =>
      genericSearch<IWidget>(res, ["name", "email"], query)
    )
    .filter((res) => genericFilter<IWidget>(res, activeFilters))
const columns = [
  {
    name: "id",
    label: "ID",
    options: show,
  },
  {
    name: "name",
    label: "Name",
    options: show
  },
  {
    name: "email",
    label: "Email",
    options: show
  },
  {
    name: "phone",
    label: "Phone",
    options: show
  },
  {
    name: "website",
    label: "Website",
    options: show
  },
  {
    name: "company",
    label: "Company",
    options: show
  },

]

var table = document.getElementById("mstrTable");
var thead = table.getElementsByTagName("thead")[0];
var tbody = table.getElementsByTagName("tbody")[0];
var ishigh;

tbody.onclick = function (e) {
  e = e || window.event;
  var td = e.target || e.srcElement; //assumes there are no other elements inside the td
  var row = td.parentNode;
  if (ishigh&&ishigh!=row){
    ishigh.className='';
  }
  row.className = row.className==="highlighted" ? "" : "highlighted";
  ishigh=row;
}

thead.onclick = function (e) {
  e = e || window.event;
  var th = e.target || e.srcElement;  //assumes there are no other elements in the th
  //alert(th.innerHTML);  ### FOR LATER (DB BACK END USE) ###
}


function rowindex(row){
	var rows = table.rows, i = rows.length;
	while(--i > -1){
		if(rows[i] === row){return i;}
	}
}

document.onkeydown = function(e){

	var code = e.keyCode, rowslim = table.rows.length - 2, newhigh;
	if(code === 38){ //up arraow
		newhigh = rowindex(ishigh) - 2;
		if(!ishigh || newhigh < 0){return GoTo('mstrTable', rowslim);}
		return GoTo('mstrTable', newhigh);
	} else if (code === 40){ //down arrow
		newhigh = rowindex(ishigh);
		if(!ishigh || newhigh > rowslim){return GoTo('mstrTable', 0);}
		return GoTo('mstrTable', newhigh);
	}
};
  return (
    <div className="App">
      {resultdata.length > 0 && (
        <Container>
          <Row md={4}>
            <Col style={{marginTop:"5em", marginBottom:"2em"}}>
              <SearchInput onChangeSearchQuery={(query) => setQuery(query)} />
              {/* <FilterFields /> */}
            </Col>
            <Col >
              <table id="mstrTable">
                <tr>
                  {columns.map((column) => (
                    column.options ? <th>{column.label}</th> :  ''
                  )
                  )}
                </tr>
                {resultdata.map((widget) => {
                  const emailtTo = widget.email;
                  const phone = widget.phone;
                  return (
                  <tr>
                    <td>{widget.id}</td>
                    <td>{widget.name}</td>
                    <td><a style={{textDecoration:"none"}} href={`mailto:${emailtTo}`}>{widget.email}</a></td>
                    <td><a style={{textDecoration:"none"}} href={`tel:${phone}`}>{widget.phone}</a></td>
                    <td>{widget.website}</td>
                    <td>{widget.company.name}</td>
                  </tr>
                  )
                  })}
                <div>
                </div>
              </table>


            </Col>
          </Row>
        </Container>
      )}
        {resultdata.length === 0 && <p>No results found!</p>}
    </div>
  );
}

export default App;
