import * as React from "react";
import { ReactNode } from "react";
import IFilter from "../interfaces/IFilter";

export interface IFiltersProps<T> {
  object: T;
  filters: Array<IFilter<T>>;
  onChangeFilter: (
    filterProperty: keyof T,
    checked: boolean,
    isTruthyPicked: boolean
  ) => void;
}

export function Filters<T>(props: IFiltersProps<T>) {
  const { object, filters, onChangeFilter } = props;

  const labelTruthy = (
    <>
      is <b>truthy</b>
    </>
  );

  const labelFalsy = (
    <>
      is <b>falsy</b>
    </>
  );
const filterSelection = () => {
  alert("all");
}

  return (
    <div className="p-1 my-2">
      <label className="mt-3">Filters</label>
      {Object.keys(object).map((key) => {
        const getRadioButton = (isTruthyPicked: boolean): ReactNode => {
          const id = isTruthyPicked
            ? `radio-defined-${key}`
            : `radio-not-defined-${key}`;
          const label = isTruthyPicked
            ? labelTruthy
            : labelFalsy;

          const getChecked = () => {
            const x = filters.filter(x => x.property === key);
            return x.length === 1 && x[0].isTruthyPicked === isTruthyPicked;
          }

          return (
            <>
              <input
                type="radio"
                id={id}
                value={key}
                checked={getChecked()}
                onChange={(event) =>
                  onChangeFilter(key as any, event.target.checked, isTruthyPicked)
                }
                className={"m-1 ml-3"}
              />
              <label htmlFor={id}>
                '{key}' {label}
              </label>
            </>
          );
        };

        return (
          // <div key={key} className="input-group my-3">
          //   {getRadioButton(true)}
          //   {getRadioButton(false)}
          // </div>
          <div id="myBtnContainer">
          <button className="btn active" onclick={filterSelection('all')}> Show all</button>
          <button className="btn" onclick="filterSelection('nature')"> Nature</button>
          <button className="btn" onclick="filterSelection('cars')"> Cars</button>
          <button claclassNamess="btn" onclick="filterSelection('people')"> People</button>
        </div>
        );
      })}
    </div>
  );
}
