import React, { useState } from "react";
import './App.css';
import SearchInput from "./components/SearchInput";
import IWidget from "./interfaces/IWidget"
import { WidgetCard } from "./components/WidgetCard";
import IFilter from "./interfaces/IFilter";
import { genericSearch } from "./utils/genericSearch";
import { genericFilter } from "./utils/genericFilter";

function App() {
  const [query, setQuery] = useState<string>("");
  const [data, setData] = useState([]);

  const [activeFilters, setActiveFilters] = useState<Array<IFilter<IWidget>>>(
    []
  );

  const resultdata = data
    .filter((res) =>
      genericSearch<IWidget>(res, ["name", "email"], query)
    )
    .filter((res) => genericFilter<IWidget>(res, activeFilters))
    // .sort((widgetA, widgetB) =>
    //   genericSort<IWidget>(widgetA, widgetB, activeSorter)
    // );

  return (
    <div className="App">
      <SearchInput onChangeSearchQuery={(query) => setQuery(query)} />

      <h3>Results:</h3>
      {resultdata.length > 0 && (
        <div className="row">
          {resultdata.map((widget) => (
            <WidgetCard key={widget.id} {...widget} />
          ))}
        </div>
      )}
      {resultdata.length === 0 && <p>No results found!</p>}
    </div>
  );
}

export default App;
